package org.thrx.service.example.util;
/**
* This class is Automatically generated by the
* build scripts. Please do not modify by hand.
* To access this class use the Version.class
*
*/
class BuildVersion {

   private static final String VERSION   = "${version}";

   private static final String POMNAME   = "${pomname}";

   private static final String BUILDDATE = "${DSTAMP}";

   private static final String BUILDTIME = "${TSTAMP}";

   private static final String BUILDDAY  = "${TODAY}";

   private static final String BUILDUSER = "${user.name}";

   /**
    * getValue
    * @param param e.g. VERSION, BUILDDATE, BUILDTIME, BUILDDAY
    * @return the value from the build
    */
   public static String getValue(String param) {
      if ("VERSION".equals(param)) {
         return VERSION;
      } else if ("POMNAME".equals(param)) {
         return POMNAME;
      } else if ("BUILDDATE".equals(param)) {
         return BUILDDATE;
      } else if ("BUILDTIME".equals(param)) {
         return BUILDTIME;
      } else if ("BUILDDAY".equals(param)) {
         return BUILDDAY;
      } else if ("BUILDUSER".equals(param)) {
         return BUILDUSER;
      } else {
         return "Unknown param: "+param+"";
      }
   }
}
